'use strict'
/* eslint-env mocha */

const { expect } = require('chai')
const main = require('.')

describe('The main module interface', function () {
  it('Is a function', function (done) {
    expect(main).to.be.a.function
    done()
  })

  it('Takes one parameter', function (done) {
    expect(main.length).to.equal(1)
    done()
  })

  it('Returns an object', function (done) {
    expect(main({})).to.be.an.object
    done()
  })
})
