'use strict'
/* eslint-env mocha */

const { expect } = require('chai')
const runModule = require('.')

describe('The run module interface', function () {
  it('Is a function', function (done) {
    expect(runModule).to.be.a.function
    done()
  })

  it('Takes one parameter', function (done) {
    expect(runModule.length).to.equal(1)
    done()
  })

  it('Returns an object', function (done) {
    expect(runModule({})).to.be.an.object
    done()
  })
})
